package learn.java;

public class BreakContinuousLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int lottery = 20;
		
		for(int i =0; i<=lottery; i ++) {
			
			if(i == 20) { 
				
				System.out.println("I value is 10 skip the looping");
			}
			else 
				System.out.println(i + " Print I value is greater than lottery");
			
			
			
		}
		
		 
			
			
			
	}
		
	
	
	
	
	}


