package loopingstatement;

public class summationProgramPractise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=10;
		int sum = 0;
		
		for(int i=0; i<=n;i++) {
			
			
			sum = sum+i;
			
			System.out.println(sum);

			
		}
		
		System.out.println("Total value of sum is " + sum);

	}

}
